INSERT INTO wp_options VALUES("190","wp_sh_comment_hl_description_before","This site supports <a href=\"http://alexgorbatchev.com/SyntaxHighlighter/\">SyntaxHighlighter</a> via <a href=\"http://www.near-mint.com/blog/software/wp-syntaxhighlighter\">WP SyntaxHighlighter</a>. It can highlight your code. <br /><strong>How to highlight your code:</strong> Paste your code in the comment form, select it and then click the language link button below. This will wrap your code in a <pre> tag(or shortcode) and format it when submitted.","yes");
INSERT INTO wp_options VALUES("191","wp_sh_comment_hl_stylesheet",".comment_highlight {\nmargin: 0px 0px 0px 0px;\n}\n.comment_highlight p {\nfont-size: 12px;\n}\n.comment_highlight_button {\nmargin: 0px 0px 0px 0px;\nfont-size: 10px;\n}","yes");
